import React, { Component } from "react";
import Konva from "konva";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text, Circle, Line } from "react-konva";

class App extends Component {
  componentDidMount() {
    setInterval(() => {
      fetch("http://localhost:5000/")
        .then(res => res.json())
        .then(data => {
          this.setState({ location: data });
          console.log(this.state.location);
        })
        .catch(console.log);
    }, 250);
  }
  render() {
    if (this.state) {
      // console.log(this.state.location);
      const data = this.state.location
      console.log(data)

      // const listItems = data.map((d)=> {
      //   console.log(d)
      // })

      for (var key in data) {
        if (data.hasOwnProperty(key)) {
          var val = data[key];
          console.log(val);
          return (
            // <div style={{fontSize: 60}} >{key}</div>
            <div>
            <Stage width={window.innerWidth} height={window.innerHeight}>
              <Layer>

                <Circle

                  x={(this.state) ? (600 - Number(this.state.location.Jeremy.latest_left)+35) : (150)}
                  y= {(this.state) ? (this.state.location.Jeremy.latest_top) : (150)}
                  radius={38}
                  fill="#1C89BF"
                />
                <Text text="Si Han" fontSize={15} 
                x ={(this.state != null) ? (600 - Number(this.state.location.Jeremy.latest_left)+35) : (150)}
                y={(this.state) ? (this.state.location.Jeremy.latest_top) : (150)}
                />

              </Layer>
              {/* <Layer>

                <Circle

                  x={(this.state) ? (600 - Number(this.state.location.Suyash.latest_left)+35) : (150)}
                  y= {(this.state) ? (this.state.location.Suyash.latest_top) : (150)}
                  radius={38}
                  fill="#1C89BF"
                />
                <Text text="Suyash" fontSize={15} 
                x ={(this.state) ? (600 - Number(this.state.location.Suyash.latest_left)+35) : (150)}
                y={(this.state) ? (this.state.location.Suyash.latest_top) : (150)}
                />

              </Layer> */}

            </Stage>
            {(this.state) ? (<div>{this.state.location.y}</div>) : (<div>Not defined</div>)}
          </div>


          )
        }
      }

    }

    return (
      <div>
        <Stage width={window.innerWidth} height={window.innerHeight}>
          <Layer>

            <Circle

              x={(this.state) ? (600 - Number(this.state.location.Jeremy.latest_left)+35) : (150)}
              y= {(this.state) ? (this.state.location.Jeremy.latest_top) : (150)}
              radius={38}
              fill="#1C89BF"
            />
            <Text text="Si Han" fontSize={15} 
            x ={(this.state != null) ? (600 - Number(this.state.location.Jeremy.latest_left)+35) : (150)}
            y={(this.state) ? (this.state.location.Jeremy.latest_top) : (150)}
            />

          </Layer>
          {/* <Layer>

            <Circle

              x={(this.state) ? (600 - Number(this.state.location.Suyash.latest_left)+35) : (150)}
              y= {(this.state) ? (this.state.location.Suyash.latest_top) : (150)}
              radius={38}
              fill="#1C89BF"
            />
            <Text text="Suyash" fontSize={15} 
            x ={(this.state) ? (600 - Number(this.state.location.Suyash.latest_left)+35) : (150)}
            y={(this.state) ? (this.state.location.Suyash.latest_top) : (150)}
            />

          </Layer> */}

        </Stage>
        {(this.state) ? (<div>{this.state.location.y}</div>) : (<div>Not defined</div>)}
      </div>
    );
  }
}

export default App;
