import React, { Component } from "react";
import Konva from "konva";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text, Circle, Line } from "react-konva";

class App extends Component {
  componentDidMount() {
    setInterval(() => {
      fetch("http://localhost:5000/")
        .then(res => res.json())
        .then(data => {
          this.setState({ location: data });
          console.log(this.state.location);
        })
        .catch(console.log);
    }, 250);
  }
  render() {
    if (this.state) {
      // console.log(this.state.location);
      const data = this.state.location
      console.log(data)

      let arr = []
      Object.keys(data).forEach(function(key){
        arr.push(data[key]);
      })
      return <ul>{arr.map(item => <div>item.latest_left</div>)}</ul>;

      class MyApp extends React.Component {
        render() {
          var json = {"active":{"label":"Active","value":"12"},"automatic":{"label":"Automatic","value":"8"},"waiting":{"label":"Waiting","value":"1"},"manual":{"label":"Manual","value":"3"}};
          var arr = [];
          Object.keys(json).forEach(function(key) {
            arr.push(json[key]);
          });
          return <ul>{arr.map(item => <MyAppChild key={item.label} label={item.label} value={item.value} />)}</ul>;
        }
      }

      // const listItems = data.map((d)=> {
      //   console.log(d)
      // })

      // for (var key in data) {
      //   if (data.hasOwnProperty(key)) {
      //     var val = data[key];
      //     console.log(val);
      //     return (
      //       <div style={{fontSize: 60}} >{key}</div>
      //     )
      //   }
      // }



    }

    return (
      <div>
        <Stage width={window.innerWidth} height={window.innerHeight}>
          <Layer>

            <Circle

              x={(this.state) ? (600 - Number(this.state.location.Jeremy.latest_left)+35) : (150)}
              y= {(this.state) ? (this.state.location.Jeremy.latest_top) : (150)}
              radius={38}
              fill="#1C89BF"
            />
            <Text text="Si Han" fontSize={15} 
            x ={(this.state != null) ? (600 - Number(this.state.location.Jeremy.latest_left)+35) : (150)}
            y={(this.state) ? (this.state.location.Jeremy.latest_top) : (150)}
            />

          </Layer>
          {/* <Layer>

            <Circle

              x={(this.state) ? (600 - Number(this.state.location.Suyash.latest_left)+35) : (150)}
              y= {(this.state) ? (this.state.location.Suyash.latest_top) : (150)}
              radius={38}
              fill="#1C89BF"
            />
            <Text text="Suyash" fontSize={15} 
            x ={(this.state) ? (600 - Number(this.state.location.Suyash.latest_left)+35) : (150)}
            y={(this.state) ? (this.state.location.Suyash.latest_top) : (150)}
            />

          </Layer> */}

        </Stage>
        {(this.state) ? (<div>{this.state.location.y}</div>) : (<div>Not defined</div>)}
      </div>
    );
  }
}

export default App;
