// import { bounceOut } from "eases";
import Konva from "konva";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text, Circle, Line } from "react-konva";
import { isTemplateElement } from "@babel/types";
import ls from 'local-storage'
import AnimateCircle from './AnimateCircle'
import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props)
  }
  componentDidMount() {
    setInterval(() => {
      if (this.state) {
        this.setState({oldState: this.state.location})
      }

      fetch("http://localhost:5000/")
        .then(res => res.json())
        .then(data => {
          // this.setState({ location: data });
          // console.log("ASSIGNED CURRENT STATE")
          // console.log(this.state.location);
          this.setState({
            location: data 
          });
        })


        .catch(console.log);
    }, 1000);
  }

  // this.oldState = this.state.location.x

  render() {
    console.log(this.state)

    return (
      <div>
        <AnimateCircle value={this.props} />
      </div>
    );
  }
}

export default App;
