import Animate from "react-animate-x";
// import { bounceOut } from "eases";
import Konva from "konva";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text, Circle, Line } from "react-konva";
import { isTemplateElement } from "@babel/types";
import ls from 'local-storage'

import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props)
  }
  componentDidMount() {
    setInterval(() => {
      this.setState({oldState: this.state.location})
      fetch("http://localhost:5000/")
        .then(res => res.json())
        .then(data => {
          // this.setState({ location: data });
          // console.log("ASSIGNED CURRENT STATE")
          // console.log(this.state.location);
          this.setState({
            location: data 
          });
        })


        .catch(console.log);
    }, 1000);
  }

  // this.oldState = this.state.location.x

  render() {
    console.log(this.state)

    return (
      <div>
        <Animate
          from={{
            x: (this.state) ? (400) : (150),
            y: (this.state) ? (400) : (150)
          }}
          to={{
            x: (this.state) ? (this.state.oldState.x) : (300),
            y: (this.state) ? (this.state.location.y) : (300)
          }}
        >
          {({ x, y }) => (
            <div style={{ transform: `translate(${x}px, ${y}px)` }}>
              Animating!
            </div>
          )}
        </Animate>
      </div>
    );
  }
}

export default App;
