import Animate from "react-animate-x";
// import { bounceOut } from "eases";
import Konva from "konva";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text, Circle, Line } from "react-konva";
import { isTemplateElement } from "@babel/types";

import React, { Component } from "react";

class App extends Component {
  componentDidMount() {
    setInterval(() => {
      fetch("http://localhost:5001/")
        .then(res => res.json())
        .then(data => {
          this.setState({ prevState: this.state.location})
          this.setState({ location: data });
          console.log(this.state.location);
        })
        .catch(console.log);
    }, 250);
  }

  render() {
    if (this.state) {
      // console.log(this.state.location);
      const item = this.state.location;
      console.log(item);
    }

    return (
      <div>
        {/* <Animate
          from={{
            x: 0,
            y: 0
          }}
          to={{
            x: 100,
            y: 150
          }}
          // easing={bounceOut}
        >
        <Stage width={window.innerWidth} height={window.innerHeight}>
              <Layer>               
                <Circle

                  x={(this.state) ? (1200 - Number(this.state.location.Vedant.latest_left)+30) : (300)}
                  y= {(this.state) ? (Number(this.state.location.Vedant.latest_top) + 200) : (150)}
                  radius={100}
                  fill="#1C89BF"
                />
                <Text text="Vedant" fontSize={15} 
                x ={(this.state) ? (1200 - Number(this.state.location.Vedant.latest_left)-9) : (150)}
                y={(this.state) ? (Number(this.state.location.Vedant.latest_top) + 200) : (150)}
                fill = "white"
                fontSize = {30}
                />        
              </Layer>
        {/* {(this.state) ? (<div>{this.state.location.y}</div>) : (<div>Not defined</div>)} */}
        {/* </Stage> */}
        {/* </Animate> */}
        <Animate
          from={{
            x: (this.state) ? (this.state.prevState.Vedant.latest_left) : (0),
            y: 0
          }}
          to={{
            x: (this.state) ? (1200 - Number(this.state.location.Vedant.latest_left)+30) : (300),
            y: (this.state) ? (1200 - Number(this.state.location.Vedant.latest_top)+30) : (300)
          }}
        >
          {({ x, y }) => (
            <div style={{ transform: `translate(${x}px, ${y}px)` }}>
              Animating!
            </div>
          )}
        </Animate>
      </div>
    );
  }
}

export default App;
