import React, { Component } from "react";

export default class AnimateCircle extends Component {
  render() {
    return (
      <div>
        <Animate
          from={{
            x: this.state ? 400 : 150,
            y: this.state ? 400 : 150
          }}
          to={{
            x: this.state ? 100 : 300,
            y: this.state ? 100 : 300
          }}
        >
          {({ x, y }) => (
            <div style={{ transform: `translate(${x}px, ${y}px)` }}>
              Animating!
            </div>
          )}
        </Animate>
      </div>
    );
  }
}
