import React, { Component } from "react";
import Konva from "konva";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text, Circle, Line } from "react-konva";
import { isTemplateElement } from "@babel/types";

class App extends Component {
  componentDidMount() {
    setInterval(() => {
      fetch("http://localhost:5000/")
        .then(res => res.json())
        .then(data => {
          this.setState({ location: data });
          console.log(this.state.location);
        })
        .catch(console.log);
    }, 250);
  }
  render() {
    if (this.state) {
      // console.log(this.state.location);
      const data = this.state.location
      console.log(data)

      let arr = []
      Object.keys(data).forEach(function(key){
        arr.push(data[key]);
        console.log(arr)
      })
      return <div>{arr.map(item => 
        <div>
        <div>
          Name is {item.name}
          Left is {item.latest_left}
          Top is {item.latest_top}
          Presence is {item.latest_timestamp}
        </div>
        <div>
                <Stage width={window.innerWidth} height={window.innerHeight}>
                <Layer>

                  <Circle

                    x={(this.state) ? (600 - Number(item.latest_left)+35) : (300)}
                    y= {(this.state) ? (item.latest_top) : (150)}
                    radius={38}
                    fill="#1C89BF"
                  />
                  <Text text={item.name} fontSize={15} 
                  x ={(this.state) ? (600 - Number(item.latest_left)+35) : (150)}
                  y={(this.state) ? (item.latest_top) : (150)}
                  />

                </Layer>


              </Stage>
              </div>
          </div>
      )}</div>;







    }

    return (
      <div>

        {/* {(this.state) ? (<div>{this.state.location.y}</div>) : (<div>Not defined</div>)} */}
      </div>
    );
  }
}

export default App;
