import Animate from "react-animate-x";
// import { bounceOut } from "eases";
import Konva from "konva";
import { render } from "react-dom";
import { Stage, Layer, Rect, Text, Circle, Line } from "react-konva";
import { isTemplateElement } from "@babel/types";

import React, { Component } from "react";

class App extends Component {
  componentDidMount() {
    setInterval(() => {
      fetch("http://localhost:5000/")
        .then(res => res.json())
        .then(data => {
          this.setState({ location: data });
          console.log("ASSIGNED CURRENT STATE")
          console.log(this.state.location);
        })
        .catch(console.log);
    }, 3500);
  }

  render() {
    console.log(this.state)
    // this.oldState = this.state.location.x
    return (
      <div>
        <Animate
          from={{
            x: (this.state) ? (this.state.oldState.oldX) : (150),
            y: (this.state) ? (0) : (150)
          }}
          to={{
            x: (this.state) ? (1200 - Number(this.state.location.x)+30) : (300),
            y: (this.state) ? (1200 - Number(this.state.location.y)+30) : (300)
          }}
        >
          {({ x, y }) => (
            <div style={{ transform: `translate(${x}px, ${y}px)` }}>
              Animating!
            </div>
          )}
        </Animate>
      </div>
    );
  }
}

export default App;
