import React from "react";
// nodejs library that concatenates classes
import classNames from "classnames";
// nodejs library to set properties for components
import PropTypes from "prop-types";
// @material-ui/core components
import withStyles from "@material-ui/core/styles/withStyles";
// @material-ui/icons

export default class DynamicSeat2 extends React.Component {
  constructor(props) {
    super(props);
    this.value2 = 0;
  }

  componentDidMount() {
    setInterval(() => {
      fetch("http://localhost:500/")
        .then(res => res.json())
        .then(data => {
          this.setState({ database: data });
          console.log(this.state.database);
        })
        .catch(console.log);
    }, 1500);
  }
  getColor = value => {
    var hue_array = ["#00BF2C", "#ACFF4F", "#C6C013", "#EF8A17", "#EF2917"];
    switch (value) {
      case 5:
        return hue_array[4];
      case 4:
        return hue_array[3];
      case 3:
        return hue_array[2];
      case 2:
        return hue_array[1];
      case 1:
        return hue_array[0];
      default:
        return "FFFFFF";
    }
  };

  render() {
    if (this.state) {
      this.value2 = this.state.database.person2;
    }
    return (
      <div
        style={{
          marginLeft: "200px",
          filter: "drop-shadow(0px 0px 0px white)"
        }}
      >
        <div
          style={{
            float: "left",
            marginRight: "60px",
            marginTop: "40px",
            height: "60px",
            width: "70px",
            background: "white",
            color: "white",
            textAlign: "center",
            verticalAlign: "end",
            position: "relative",
            borderRadius: "7%"
          }}
        >
          <div
            style={{
              textAlign: "center",
              verticalAlign: "middle",
              marginTop: "20px",
              fontSize: "13px"
            }}
          >
            Empty
          </div>
        </div>
      </div>
    );
  }
}
